import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-opcion-item',
  templateUrl: './opcion-item.component.html',
  styleUrls: ['./opcion-item.component.css']
})
export class OpcionItemComponent {
  @Input() opcion: string = ""

  onMouseOver() {
    const rect = document.getElementById("rect")
    rect?.setAttribute("y", "1")
    rect?.setAttribute("style", "width: 120px; height: 20px; transition: all 0.2s ease")
  }

  onMouseOut() {
    const rect = document.getElementById("rect")
    rect?.setAttribute("y", "8")
    rect?.setAttribute("style", "width: 8px; height: 8px; transition: all 0.2s ease")
  }
}
