import { AfterViewInit, Component, ElementRef, HostListener, Renderer2 } from '@angular/core';
import { NavService } from 'src/app/services/nav.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements AfterViewInit {
  navOpcion: string = ''

  constructor(
    private navService: NavService,
    private el: ElementRef,
    private renderer: Renderer2
  ){
    navService.getNavOpcion().subscribe(nav => this.navOpcion = nav)
  }

  ngAfterViewInit() {
    this.seguirCursor()
  }

  seguirCursor() {
    document.addEventListener("mousemove", (event: MouseEvent) => {
      const follower = document.getElementById("follower") as HTMLElement
      const x = event.clientX
      const y = event.clientY

      if(follower){
        follower.style.display = "block"
        follower.style.left = x + "px"
        follower.style.top = y + "px"
      }
    })
  }

  @HostListener('window:scroll', ['$event'])
  onScroll() {
    const scrollY = window.scrollY

    const headerHeight = 60

    const seccionSeleccionada = this.el.nativeElement.querySelector('.seccion-seleccionada')

    if(seccionSeleccionada){
      const isDebajo = scrollY > headerHeight

      if(isDebajo){
        this.renderer.addClass(seccionSeleccionada, 'debajo-header')
      } else {
        this.renderer.removeClass(seccionSeleccionada, 'debajo-header')
      }
    }
  }
}
